CREATE TABLE paints (`id` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `room` VARCHAR(255) NOT NULL, `code` VARCHAR(50) NOT NULL, `deleted` TINYINT(1) NULL );
CREATE TABLE readings (`id` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `reading` INT NOT NULL, `day` DATE NOT NULL, `type` INT NOT NULL, `deleted` TINYINT(1) NULL );
CREATE TABLE reading_type (`id` INT PRIMARY KEY AUTO_INCREMENT, `type` VARCHAR(50) NOT NULL );